// image to render on screen
const image = new Image();
image.src =
  "data:image/jpeg;base64,";

image.addEventListener("load", () => {
  const canvas_id = document.getElementById("canvas1");
  const ctx = canvas_id.getContext("2d");

  canvas_id.width = 500;
  canvas_id.height = 706;

  //   draw image
  /**
   * 1. image url
   * 2. top left corner of canvas
   * 3. cover all canvas
   */
  ctx.drawImage(image, 0, 0, canvas_id.width, canvas_id.height);
  const pixels = ctx.getImageData(0, 0, canvas_id.width, canvas_id.height);
  ctx.clearRect(0, 0, canvas_id.width, canvas_id.height);

  let particleArray = [];
  const numberOfParticle = 10000;

  let mappedImage = [];

  for (let y = 0; y < canvas_id.height; y++) {
    let row = [];
    for (let x = 0; x < canvas_id.width; x++) {
      const red = pixels.data[y * 4 * pixels.width + x * 4];
      const green = pixels.data[y * 4 * pixels.width + (x * 4 + 1)];
      const blue = pixels.data[y * 4 * pixels.width + (x * 4 + 2)];
      const color = "rgb(" + red + "," + green + "," + blue + ")";
      const brightness = calculateRelativeBrightness(red, green, blue) / 100;
      const cell = [color,brightness];
      row.push(cell);
    }
    mappedImage.push(row);
  }

  function calculateRelativeBrightness(red, green, blue) {
    return Math.sqrt(
      red * red * 0.299 + green * green * 0.587 + blue * blue * 0.114
    );
  }

  class Particle {
    constructor() {
      this.x = Math.random() * canvas_id.width;
      this.y = 0;
      this.speed = 0;
      this.velocity = Math.random() * 1.5;
      this.size = Math.random() * 1.5 + 1;
      this.position1 = Math.floor(this.y);
      this.position2 = Math.floor(this.x);
    }

    update() {
      this.position1 = Math.floor(this.y);
      this.position2 = Math.floor(this.x);
      this.speed = mappedImage[this.position1][this.position2][1];

      let movment = 2.5 - this.speed + this.velocity;
      // particles fall randomly
      this.y += movment;
      if (this.y >= canvas_id.height) {
        this.y = 0;
        this.x = Math.random() * canvas_id.width;
      }
    }
    draw() {
      ctx.beginPath();
      if (this.y > canvas_id.height - this.size * 6) ctx.globalAlpha = 0;
      if (mappedImage[this.position1]){
        if (mappedImage[this.position1][this.position2]){
            ctx.fillStyle = mappedImage[this.position1][this.position2][0];
        }

    } else {
        ctx.fillStyle = 'white';
    }
    //   ctx.fillStyle = "white";
      ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
      ctx.fill();
    }
  }
  function init() {
    for (let i = 0; i < numberOfParticle; i++) {
      particleArray.push(new Particle());
    }
  }
  init();

  function animate() {
    // ctx.drawImage(image, 0, 0, canvas_id.width, canvas_id.height);

    ctx.globalAlpha = 0.05;
    ctx.fillStyle = "rgb(0,0,0)";
    ctx.fillRect(0, 0, canvas_id.width, canvas_id.height);
    ctx.globalAlpha = 0.2;

    for (let i = 0; i < particleArray.length; i++) {
      particleArray[i].update();
      ctx.globalAlpha = particleArray[i].speed * 0.5;
      particleArray[i].draw();
    }
    requestAnimationFrame(animate);
  }

  animate();
});
